import "./App.css";
import { useEffect } from "react";
import axios from "axios";
import XMLParser from "react-xml-parser";

const xmlToJson = (dataSet) => {
  const dataArr = new XMLParser().parseFromString(dataSet).children;
  console.log(dataArr);
};

const isJsonString = (str) => {
  try {
    let json = JSON.parse(str);
    return typeof json === "object";
  } catch (e) {
    return false;
  }
};

function App() {
  useEffect(() => {
    const getAPI = async () => {
      axios
        .get(
          `http://openapi.data.go.kr/openapi/service/rest/Covid19/getCovid19SidoInfStateJson?serviceKey=et%2BHzIp9xULbk7BU%2F07ziyDd9rkLsS6ZqNDwPfF7v7a7XjnjWuyUqp9QZvicWD%2Fo1KZr3YmtyXkQAtnpqgqX9Q%3D%3D`
        )
        .then((response) => {
          const dataSet = response.data.response.body.items;
          isJsonString(response.data);
          console.log(dataSet);
          xmlToJson(dataSet);
        });
    };
    getAPI();
  }, []);
  return <div></div>;
}

export default App;
